CREATE DATABASE dungeonQuest;
USE dungeonQuest;

CREATE TABLE Questoes(
idQuestao INT PRIMARY KEY auto_increment,
materia VARCHAR (40) NOT NULL,
enunciado VARCHAR(210) NOT NULL,
alternativa_a VARCHAR (60) NOT NULL,
alternativa_b VARCHAR (60) NOT NULL,
alternativa_c VARCHAR (60) NOT NULL,
alternativa_d VARCHAR (60) NOT NULL,
resposta_correta VARCHAR (60) NOT NULL,
justificativa VARCHAR(210) NOT NULL
);

CREATE TABLE Sala(
idSala INT PRIMARY KEY AUTO_INCREMENT,
materia VARCHAR(40) NOT NULL,
tokenSala VARCHAR(6) NOT NULL,
qntdJogadores INT NOT NULL,
levels INT NOT NULL,
donoSala INT NOT NULL UNIQUE
FOREIGN KEY (materia) REFERENCES Questoes(materia)
);

CREATE TABLE Usuario(
idUsuario INT PRIMARY KEY AUTO_INCREMENT,
idSala INT,
loginUsuario VARCHAR(30) NOT NULL UNIQUE,
senhaUsuario VARCHAR(30) NOT NULL
FOREIGN KEY (idSala) REFERENCES Sala(idSala)
);

CREATE TABLE UsuarioSala(
idSala INT NOT NULL,
idUsuario INT
FOREIGN KEY (idUsuario) REFERENCES Usuario(idUsuario)
);
